import { Box, Tooltip, Typography, useMediaQuery } from "@mui/material";
import dynamic from "next/dynamic";
import Image from "next/image";
import { useRouter } from "next/router";
import { useState } from "react";
import { toast } from "react-hot-toast";
import { useHotkeys } from "react-hotkeys-hook";
import { useSession } from "../../../lib/client/useSession";
import { toastStyles } from "../../../lib/client/useTheme";
import { updateSettings } from "../../Settings/updateSettings";
import { openSpotlight } from "./Search";
import UserMenu from "./UserMenu";
const SearchPopup = dynamic(() => import("./Search"));

export function Sidebar() {
  const router = useRouter();
  const [clickCount, setClickCount] = useState(0);
  const isMobile = useMediaQuery("(max-width: 600px)");

  // Easter egg #1
  const handleClick = () => {
    setClickCount(clickCount + 1);
    if (clickCount === 9) {
      toast.success(
        <Box>
          <Typography>You found an easter egg</Typography>
          <Typography variant="body2">
            Changes the theme color to monochrome
          </Typography>
        </Box>,
        {
          ...toastStyles,
          icon: "ðŸ¥š",
        }
      );
      updateSettings("color", "blueGrey");
      setClickCount(0);
    }
  };

  useHotkeys(
    "ctrl+shift+1",
    (e) => {
      e.preventDefault();
      router.push("/zen");
    },
    [open]
  );

  useHotkeys(
    "ctrl+shift+4",
    (e) => {
      e.preventDefault();
      router.push("/items");
    },
    [open]
  );
  useHotkeys(
    "ctrl+shift+3",
    (e) => {
      e.preventDefault();
      router.push("/coach");
    },
    [open]
  );
  useHotkeys(
    "ctrl+shift+2",
    (e) => {
      e.preventDefault();
      router.push("/tasks");
    },
    [open]
  );
  const session = useSession();
  const styles = (active: any = false) => {
    return {
      color: session.user.darkMode ? "hsl(240,11%,90%)" : "hsl(240,11%,30%)",
      borderRadius: 3,
      my: 0.5,
      maxHeight: "9999px",
      overflow: "visible",
      "& .material-symbols-rounded, & .material-symbols-outlined": {
        transition: "none",
        height: 50,
        width: 50,
        display: "flex",
        alignItems: "center",
        borderRadius: 5,
        justifyContent: "center",
      },
      "&:hover .material-symbols-outlined": {
        background: session.user.darkMode
          ? "hsl(240,11%,14%)"
          : router.asPath === "/zen" || router.asPath === "/coach"
          ? "hsl(240,11%,93%)"
          : "hsl(240,11%,90%)",
        color: session.user.darkMode ? "#fff" : "#000",
      },
      "&:focus-visible span": {
        boxShadow: session.user.darkMode
          ? "0px 0px 0px 1.5px hsl(240,11%,50%) !important"
          : "0px 0px 0px 1.5px #000 !important",
      },
      userSelect: "none",
      ...(active && {
        " .material-symbols-outlined,  .material-symbols-rounded": {
          background: session.user.darkMode
            ? "hsl(240,11%,17%)"
            : router.asPath === "/zen" || router.asPath === "/coach"
            ? "hsl(240,11%,90%)"
            : "hsl(240,11%,85%)",
          color: session.user.darkMode
            ? "hsl(240,11%,95%)"
            : "hsl(240,11%,10%)",
        },
      }),
    };
  };

  return (
    <Box
      sx={{
        display: { xs: "none", md: "flex!important" },
        maxWidth: "85px",
        width: "80px",
        zIndex: "99!important",
        filter: "none!important",
        overflowX: "hidden",
        background: {
          sm: session.user.darkMode
            ? router.asPath === "/zen" || router.asPath === "/coach"
              ? "hsla(240,11%,8%)"
              : "hsla(240,11%,5%)"
            : router.asPath === "/zen" || router.asPath === "/coach"
            ? "hsl(240,11%,97%)"
            : "hsl(240,11%,93%)",
        },
        height: "100vh",
        backdropFilter: "blur(10px)",
        position: "fixed",
        alignItems: "center",
        flexDirection: "column",
        justifyContent: "center",
      }}
    >
      <Image
        onClick={() => {
          window.scrollTo({
            top: 0,
            behavior: "smooth",
          });
          handleClick();
        }}
        src="/logo.svg"
        placeholder="blur"
        blurDataURL="data:image/png;base64,"
        width={50}
        height={50}
        alt="Logo"
        style={{
          borderRadius: "999px",
          marginTop: "15px",
          ...(session.user.darkMode && {
            filter: "invert(100%)",
          }),
        }}
      />
      <Box sx={{ mt: "auto", pt: 4 }} />
      <Box
        sx={styles(
          router.asPath.includes("/zen") ||
            router.asPath === "/" ||
            router.asPath === ""
        )}
        onClick={() => router.push("/zen")}
        onMouseDown={() => router.push("/zen")}
      >
        <Tooltip title="Start" placement="right">
          <span
            className={`material-symbols-${
              router.asPath.includes("/zen") ||
              router.asPath === "/" ||
              router.asPath === ""
                ? "rounded"
                : "outlined"
            }`}
          >
            change_history
          </span>
        </Tooltip>
      </Box>
      <Box
        sx={styles(router.asPath.includes("/tasks"))}
        onClick={() => router.push("/tasks")}
        onMouseDown={() => router.push("/tasks")}
      >
        <Tooltip title="Lists" placement="right">
          <span
            className={`material-symbols-${
              router.asPath.includes("/tasks") ? "rounded" : "outlined"
            }`}
          >
            check_circle
          </span>
        </Tooltip>
      </Box>
      <Box
        sx={styles(router.asPath === "/coach")}
        onClick={() => router.push("/coach")}
        onMouseDown={() => router.push("/coach")}
      >
        <Tooltip title="Coach" placement="right">
          <span
            className={`material-symbols-${
              router.asPath === "/coach" ? "rounded" : "outlined"
            }`}
          >
            rocket_launch
          </span>
        </Tooltip>
      </Box>
      <Box
        sx={styles(
          router.asPath === "/items" ||
            router.asPath === "/trash" ||
            router.asPath === "/starred" ||
            router.asPath.includes("rooms")
        )}
        onClick={() => router.push("/items")}
        onMouseDown={() => router.push("/items")}
      >
        <Tooltip title="Items" placement="right">
          <span
            className={`material-symbols-${
              router.asPath === "/items" ||
              router.asPath === "/trash" ||
              router.asPath === "/starred" ||
              router.asPath.includes("rooms")
                ? "rounded"
                : "outlined"
            }`}
          >
            inventory_2
          </span>
        </Tooltip>
      </Box>

      <Box
        sx={{
          mt: "auto",
          mb: 1,
          alignItems: "center",
          display: "flex",
          flexDirection: "column",
        }}
      >
        <SearchPopup />
        <Box
          onClick={() => openSpotlight()}
          onMouseDown={() => openSpotlight()}
          sx={styles(false)}
        >
          <Tooltip
            title={
              <>
                <Typography sx={{ fontWeight: 700 }}>Spotlight</Typography>
                <Typography>ctrl &bull; k</Typography>
              </>
            }
            placement="right"
          >
            <span className="material-symbols-outlined">bolt</span>
          </Tooltip>
        </Box>
        {!isMobile && <UserMenu styles={styles} />}
      </Box>
    </Box>
  );
}
